<script>var albummoduleid = '<?php echo $albummoduleid; ?>';var logomoduleid = '<?php echo $logomoduleid; ?>';    layui.use(['form','upload','layedit','laydate','jquery','rate'], function () {        var form = layui.form,            upload = layui.upload,            layedit = layui.layedit,            laydate = layui.laydate,            $ = layui.jquery,            rate = layui.rate;        //自定义验证规则        form.verify({        });        //监听提交        form.on('submit(submit)', function (data) {            // 提交到方法 默认为本身            var loading = layer.load(1, {shade: [0.1, '#fff']});            $.post("", data.field, function (res) {                layer.close(loading);                if (res.code > 0) {                    layer.msg(res.msg, {time: 1800, icon: 1}, function () {                        location.href = res.url;                    });                } else {                    layer.msg(res.msg, {time: 1800, icon: 2});                }            });        });        //监听省份选择        form.on('select(province)', function(data){            var indexGID = data.elem.selectedIndex;            //获取当前选中下拉项的自定义属性值            var dataid = data.elem[indexGID].title;            $('#city').html('<option value="">请选择市/县</option>');            $('#area').html('<option value="">请选择镇区</option>');            $('#street').html('<option value="">请选择街道</option>');            $.ajax( {                url:"<?php echo url('admin/bigshop/getchildarea'); ?>",                data: {parent_id: dataid},                type:'POST',                dataType:'json',                success:function(data1) {                    if (data1.error == 0) {                        $("#city").append(data1.option);                        form.render('select'); //刷新select选择框渲染                    }                }            });        });        form.on('select(city)', function(data){            var indexGID = data.elem.selectedIndex;            //获取当前选中下拉项的自定义属性值            var dataid = data.elem[indexGID].title;            $('#area').html('<option value="">请选择镇区</option>');            $('#street').html('<option value="">请选择街道</option>');            $.ajax( {                url:"<?php echo url('admin/bigshop/getchildarea'); ?>",                data: {parent_id: dataid},                type:'POST',                dataType:'json',                success:function(data1) {                    if (data1.error == 0) {                        $("#area").append(data1.option);                        form.render('select'); //刷新select选择框渲染                    }                }            });        });        //监听省份选择        form.on('select(area)', function(data){            var indexGID = data.elem.selectedIndex;            //获取当前选中下拉项的自定义属性值            var dataid = data.elem[indexGID].title;            $('#street').html('<option value="">请选择街道</option>');            $.ajax( {                url:"<?php echo url('admin/bigshop/getchildarea'); ?>",                data: {parent_id: dataid},                type:'POST',                dataType:'json',                success:function(data1) {                    if (data1.error == 0) {                        $("#street").append(data1.option);                        form.render('select'); //刷新select选择框渲染                    }                }            });        });        //日期        laydate.render({            elem: '#date'        });        laydate.render({            elem: '#date1'        });        //时间范围        laydate.render({            elem: '#test9'            ,type: 'time'            ,range: true        });        //星级        rate.render({            elem: '#test4'            ,value: 5            ,half: true            ,text: true            ,setText: function(value){ //自定义文本的回调                var arrs = {                    '1': '极差'                    ,'2': '差'                    ,'3': '中等'                    ,'4': '好'                    ,'5': '极好'                };                this.span.text(arrs[value]);                $("#number").val(value);            }        })        //商圈logo         upload.render({            elem: '.upload-picture',            url: uploadApi,            data: {                moduleid: logomoduleid,                attachmark: attachmark,                identity: identity,            },            field: 'upfile',            //size: 20000,            exts: 'jpg|png|jpeg',            before: function(obj) {                layer.msg('图片上传中...', {                    icon: 16,                    shade: 0.01,                    time: 0                })            },            done: function(res, index, upload){ //上传后的回调                layer.msg(res.message);                if( res.status == 'success' ){                    //if(res.uploadInfo.attachthumb == ''){                        $('input[name=bigshoplogo]').val(res.uploadInfo.attachurl);                        $('img','.upload-picture').attr('src',res.uploadInfo.attachurl);                    // }else{                    //     $('input[name=shoplogo]').val(res.uploadInfo.attachthumb);                    //     $('img','.upload-picture').attr('src',res.uploadInfo.attachthumb);                    // }                }            }        });        //多图上传        upload.render({            elem: '#slide-pc',             url: uploadApi,            data: {                moduleid: albummoduleid,                attachmark: attachmark,                identity: identity,            },            field: 'upfile',            //size: 100000000000,            exts: 'jpg|png|jpeg|gif',            multiple: true,            before: function(obj) {                layer.msg('图片上传中...', {                    icon: 16,                    shade: 0.01,                    time: 0                })            },            done: function(res) {                layer.close(layer.msg());//关闭上传提示窗口                if(res.status == 'success') {                    $('#slide-pc-priview').append('<li class="item_img"><div class="operate"><i class="toleft layui-icon"></i><i class="toright layui-icon"></i><i  class="close layui-icon"></i></div><img src="' + res.uploadInfo.attachurl + '" class="img" ><input type="hidden" name="headimg[]" value="' + res.uploadInfo.attachurl + '" /></li>');                }else{                    return layer.msg(res.message);                }                            }        });    });    //点击多图上传的X,删除当前的图片    $("body").on("click",".close",function(){        $(this).closest("li").remove();    });    //多图上传点击<>左右移动图片    $("body").on("click",".pic-more ul li .toleft",function(){        var li_index=$(this).closest("li").index();        if(li_index>=1){            $(this).closest("li").insertBefore($(this).closest("ul").find("li").eq(Number(li_index)-1));        }    });    $("body").on("click",".pic-more ul li .toright",function(){        var li_index=$(this).closest("li").index();        $(this).closest("li").insertAfter($(this).closest("ul").find("li").eq(Number(li_index)+1));    });    $("body").on("click",".mapkg",function(){         var dis=$('#g-map').css('display');         if (dis=='block') {            dis=$('#g-map').css('display','none')            $(this).text('打开地图选择')         } else {            dis=$('#g-map').css('display','block')            $(this).text('关闭地图选择')         }    });</script><script type="text/javascript" src="https://webapi.amap.com/maps?v=1.4.6&key=a739148df6737cfafd4bc1d9a35d2a1f&plugin=AMap.Autocomplete,AMap.PlaceSearch,AMap.CitySearch,AMap.Geolocation,AMap.Geocoder"></script><script type="text/javascript" src="https://cache.amap.com/lbs/static/addToolbar.js"></script><script type="text/javascript" src="https://webapi.amap.com/demos/js/liteToolbar.js"></script><script src="https://webapi.amap.com/ui/1.0/main.js"></script><link rel="stylesheet" href="https://cache.amap.com/lbs/static/main1119.css"/><script type="text/javascript">    var map,addMarker;    var geocoder;    var placeSearch;    var lat = "<?php echo (isset($franchisee['latitude']) && ($franchisee['latitude'] !== '')?$franchisee['latitude']:'39.90923'); ?>";    var lng = "<?php echo (isset($franchisee['longitude']) && ($franchisee['longitude'] !== '')?$franchisee['longitude']:'116.397428'); ?>";    map = new AMap.Map('g-map', {        resizeEnable: true,        center: [lng,lat],        zoom:13,//级别    });    //为地图注册click事件获取鼠标点击出的经纬度坐标    var clickEventListener = map.on('click', function(e) {        $("input[name='m_point']").val(e.lnglat.getLng() + ',' + e.lnglat.getLat());        $("input[name='latitude']").val(e.lnglat.getLat());        $("input[name='longitude']").val(e.lnglat.getLng());        // 创建一个 Marker 实例：        var marker = new AMap.Marker({            position: new AMap.LngLat(e.lnglat.getLng(), e.lnglat.getLat()),   // 经纬度对象，也可以是经纬度构成的一维数组[116.39, 39.9]        });        // 将创建的点标记添加到已有的地图实例：        map.add(marker);        map.on('click', function(e) {            // 移除已创建的 marker            map.remove(marker);        });        var geocoder;        var lnglatXY=new AMap.LngLat(e.lnglat.getLng(),e.lnglat.getLat());        //加载地理编码插件        map.plugin(["AMap.Geocoder"],function(){            geocoder=new AMap.Geocoder({                radius:1000, //以已知坐标为中心点，radius为半径，返回范围内兴趣点和道路信息                extensions: "all"//返回地址描述以及附近兴趣点和道路信息，默认"base"            });            //返回地理编码结果            AMap . event . addListener(geocoder, "complete",geocoder_CallBack);            //逆地理编码            geocoder.getAddress(lnglatXY);        });        //地理编码返回结果展示        function geocoder_CallBack(data){            console.log(data,'kkkkk');            var resultStr="";            //地理编码结果数组            var geocode = new Array();            geocode = data.geocodes;            $("input[name='address']").val(data.regeocode.formattedAddress);        }    });</script>


