  <script>var identity = 'ruler';var moduleid = '<?php echo $moduleid; ?>';var attachmark = '<?php echo $attachmark; ?>';var uploadApi = "<?php echo url('upload/index/uploadimage'); ?>";    layui.use(['form','upload','layedit','laydate','jquery','element'], function () {        var form = layui.form,upload = layui.upload,layedit = layui.layedit,laydate = layui.laydate,$ = layui.jquery;element = layui.element;        //多图片上传        upload.render({            elem: '#slide-pc',             url: uploadApi,            data: {                moduleid: moduleid,                attachmark: attachmark,                identity: identity,            },            field: 'upfile',            //size: 100000000000,            exts: 'jpg|png|jpeg|gif',            multiple: true,            before: function(obj) {                layer.msg('图片上传中...', {                    icon: 16,                    shade: 0.01,                    time: 0                })            },            done: function(res) {                layer.close(layer.msg());//关闭上传提示窗口                if(res.status == 'success') {                    $('#slide-pc-priview').append('<li class="item_img"><div class="operate"><i class="toleft layui-icon"></i><i class="toright layui-icon"></i><i  class="close layui-icon"></i></div><img src="' + res.uploadInfo.attachurl + '" class="img" ><input type="hidden" name="headimg[]" value="' + res.uploadInfo.attachurl + '" /></li>');                }else{                    return layer.msg(res.message);                }                            }        });        //点击多图上传的X,删除当前的图片        $("body").on("click",".close",function(){            $(this).closest("li").remove();        });        //多图上传点击<>左右移动图片        $("body").on("click",".pic-more ul li .toleft",function(){            var li_index=$(this).closest("li").index();            if(li_index>=1){                $(this).closest("li").insertBefore($(this).closest("ul").find("li").eq(Number(li_index)-1));            }        });        $("body").on("click",".pic-more ul li .toright",function(){            var li_index=$(this).closest("li").index();            $(this).closest("li").insertAfter($(this).closest("ul").find("li").eq(Number(li_index)+1));        });        form.on('submit(submit)', function (data) {            $.post('<?php echo url(); ?>', data.field, function (res) {                if (res.code > 0) {                    layer.msg(res.msg, {time: 1800, icon: 1}, function () {                        //location.href = res.url;                        window.location.href="<?php echo url('index'); ?>"                    });                } else {                    layer.msg(res.msg, {time: 1800, icon: 2});                }            });        });                $('.layui-form').on('click','#addtr',function(){             $(this).parent().parent().before('<tr><td>参数名-值</td><td><input type="text" name="parameter[]" lay-verify="" autocomplete="off" class="layui-input-inline"> - <input type="text" name="parameter_value[]" lay-verify="" autocomplete="off" class="layui-input-inline">&nbsp;&nbsp;&nbsp;<button class="layui-btn layui-btn-sm layui-btn-normal" id="deltr"><i class="layui-icon"></i> 删除</button></td></tr>');        });        $('.layui-form').on('click','#deltr',function(){            $(this).parent().parent().remove();        });    });</script>


</body>

</html>

